import{_ as l,c as i,o as a,aa as e}from"./chunks/framework.DtK4gh9F.js";const f=JSON.parse('{"title":"02 MySQL面试问题有哪些","description":"","frontmatter":{"title":"02 MySQL面试问题有哪些","author":"Herman","updateTime":"2021-08-14 13:41","desc":"Mysql","categories":"面经","tags":"Mysql","outline":"deep"},"headers":[],"relativePath":"Notes/No3Interviews/02 MySQL面试问题有哪些？.md","filePath":"Notes/No3Interviews/02 MySQL面试问题有哪些？.md","lastUpdated":1723563552000}'),o={name:"Notes/No3Interviews/02 MySQL面试问题有哪些？.md"},t=e('<h1 id="mysql-面试一般常问问题有哪些" tabindex="-1">MySQL 面试一般常问问题有哪些？ <a class="header-anchor" href="#mysql-面试一般常问问题有哪些" aria-label="Permalink to &quot;MySQL 面试一般常问问题有哪些？&quot;">​</a></h1><h3 id="innodb的记录存储相关问题" tabindex="-1">Innodb的记录存储相关问题？ <a class="header-anchor" href="#innodb的记录存储相关问题" aria-label="Permalink to &quot;Innodb的记录存储相关问题？&quot;">​</a></h3><ol><li>一条查询语句请求执行的整个过程要需要经过哪些层？</li><li>Innodb的行记录存储格式有哪些？</li><li>Compact行记录格式有哪几部分组成？</li><li>varchar 与 char的对比，怎样选择</li><li>Compact行记录中变成字段列表是如何记录字段的长度的，何时使用1个字节，何时使用2个字节？</li><li>Compact行记录中记录的头信息有哪些？</li><li>Compact行记录中头信息中的heap_no有什么作用？</li><li>MySql除了自定义的字段外还有哪些隐藏字段</li><li>什么是溢出列，什么情况会出现溢出列？</li><li>MySQL中的数据执行删除后是真的删除了吗，删除过程是怎样的？</li></ol><h3 id="innodb数据页相关问题" tabindex="-1">Innodb数据页相关问题 <a class="header-anchor" href="#innodb数据页相关问题" aria-label="Permalink to &quot;Innodb数据页相关问题&quot;">​</a></h3><ol><li>数据页有哪些部分构成？</li><li>如何服务器突然断电，Innodb是如何知道当前数据页是完整的</li><li>页目录的作用是什么？</li><li>数据页中数据的分组过程是怎样的？</li><li>Innodb在执行数据查询时，如何快速定位到某行数据的？</li></ol><h3 id="explain" tabindex="-1">explain <a class="header-anchor" href="#explain" aria-label="Permalink to &quot;explain&quot;">​</a></h3><ol><li>explain执行之后你主要关注哪些字段？</li><li>type的类型有哪些，以及执行效率的顺序是怎样的</li><li>extra出现了临时文件排序，内存关联时如何处理</li></ol><h3 id="索引" tabindex="-1">索引 <a class="header-anchor" href="#索引" aria-label="Permalink to &quot;索引&quot;">​</a></h3><ol><li>B+Tree的数据结构是怎样的？</li><li>索引非叶子节点存的实际数据，那非叶子节点存放的是什么？</li><li>页分裂的过程是怎样的？</li><li>非叶子节点中数据行存放的有哪些字段？</li><li>索引的建立的过程中会分裂，那根节点的位置会变吗，Innodb如何记录这些根节点的位置</li><li>什么是索引下推</li></ol><h3 id="表空间" tabindex="-1">表空间 <a class="header-anchor" href="#表空间" aria-label="Permalink to &quot;表空间&quot;">​</a></h3><ol><li>解释下什么是区，引入区主要解决什么问题</li><li>Innodb中区的分类有哪些</li><li>什么是碎片区，存在碎片区的意义是什么</li><li>什么是段？段的存在意义是什么？</li><li>段在分配存储空间的策略是什么</li></ol><h3 id="bufferpool" tabindex="-1">BufferPool <a class="header-anchor" href="#bufferpool" aria-label="Permalink to &quot;BufferPool&quot;">​</a></h3><ol><li>配置的BufferPool大小会在服务启动时就立即分配吗</li><li>引入BufferPool主要解决什么问题</li><li>Free链</li><li>当我们需要查询某个页面的时候，如何判断页面是否已经在缓冲页中了呢？</li><li>当数据被修改之后，如何这些修改的数据页</li><li>修改后的数据页会立即刷新到磁盘上吗</li><li>当BufferPool的分配空间被使用完了之后，数据页的淘汰策略是什么</li></ol><h3 id="事物" tabindex="-1">事物 <a class="header-anchor" href="#事物" aria-label="Permalink to &quot;事物&quot;">​</a></h3><ol><li>事物的隔离级别</li><li>脏写、脏读、不可重复度、幻读</li><li>MVCC主要解决什么问题，如何实现的MVCC</li><li>Mysql的隔离级别如何实现的</li><li>行锁、表锁、意向独占锁、意向共享锁</li></ol>',15),n=[t];function r(d,s,h,c,_,b){return a(),i("div",null,n)}const p=l(o,[["render",r]]);export{f as __pageData,p as default};
